mapper.select:
SELECT *
FROM (	SELECT inner_table.* , rnum AS row_seq
		FROM(SELECT est_id, user_id, cur_id, estname, estdate, ROWNUM as rnum
	    	 FROM estimates
             WHERE user_id = #{userId}	     
             ORDER BY est_id
             ) inner_table
	 )
WHERE rnum BETWEEN #{startNo} AND #{endNo}

SELECT prol_id, est_id, prol_kind, prod_id, anl_ans
FROM prodlists
WHERE est_id = #{value}

SELECT COUNT(*)
FROM estimates
WHERE user_id = #{value}

mapper.update:
UPDATE estimates
SET estname = #{estName}
WHERE est_id = #{estId}

UPDATE prodlists
SET prod_id = #{prodId}
WHERE prol_id = #{prolId}
